import React from 'react';
import { Nav, Navbar } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link, NavLink } from 'react-router-dom';
import { LinkContainer } from "react-router-bootstrap";
import { Home } from './components/Home';
import { CybersecurityIntro } from './CybersecurityIntro';
import { CybersecurityCybercrimes } from './components/CybersecurityCybercrimes';
import { CryptographyIntro } from './components/CryptographyIntro';
import { WhatIsData } from './components/WhatIsData';
import { TransferData } from './components/TransferData';
import { EncryptionDecryption } from './components/EncryptionDecryption';
import { CommonAlgorithms } from './components/CommonAlgorithms';
import { Algorithm3DES } from './components/Algorithm3DES';
import { AlgorithmAES } from './components/AlgorithmAES';
import { AlgorithmRSA } from './components/AlgorithmRSA';
import { AlgorithmBlowfish } from './components/AlgorithmBlowfish';
import { AlgorithmTwofish } from './components/AlgorithmTwofish';
import { NoMatch } from './components/NoMatch';
import styled from 'styled-components';

const Styles = styled.div`
    .navbar {
        background-color: white;
        border-radius: 0 15px 15px 0;
        box-shadow: 5px 0 20px 0px rgba(0,0,0,0.3);
    }

    .navbar h2 {
        font-size: 16pt;
        font-weight: bold;
        padding-top: 20px;
        padding-bottom: 10px;
    }

    .navbar-nav .nav-link {
        color: #434343;

        &:hover {
            color: #5F2D39;
        }
    }

    .navbar-nav {
        padding-bottom: 30px;
    }

    .navbar-brand {
        margin: 0;
    }

    .navbar-brand img{
        width: 40%;
        display: block;
        margin: 20px auto 16px auto;
    }

    @media screen and (max-width: 992px) {
        .navbar {
            position: fixed;
            z-index: 5;
        }

        .navbar-toggler {
            position: fixed;
            top: 50%;
            padding: 30px 0 30px 0;
        }

        --page-height: initial;

    }
`;

export const NavigationBar = () => (
    <Router>
    <Styles className="col-md-3 float-left padding-left-zero padding-right-zero full-height-percent">   
        <Navbar expand="lg" className="full-height-percent">

            <Navbar.Toggle aria-controls="basic-navbar-nav"/>
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="flex-column full-height-vh">
                    <Navbar.Brand href="/"><img src="/assets/BrandLogo.png" alt="Brand logo"/></Navbar.Brand>
                    <div className="scroll">
                        <h2>Cybersecurity</h2>
                        <LinkContainer to="/cybersecurity-intro"><Nav.Item><NavLink to="/cybersecurity-intro">Introduction</NavLink></Nav.Item></LinkContainer>
                        <Nav.Item><Nav.Link href="/cybersecurity-and-cybercrimes">Cybersecurity and Cybercrimes</Nav.Link></Nav.Item>
                        <h2>Cryptography</h2>
                        <Nav.Item><Nav.Link href="/cryptography-intro">Introduction</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/what-is-data">What is data?</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/transfer-data-securely">Transfering data securely</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/encryption-and-decryption">Encryption and Decryption</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/common-algorithms">Commonly used algorithms</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/3des">3DES</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/aes">AES</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/rsa">RSA</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/blowfish">Blowfish</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link href="/twofish">Twofish</Nav.Link></Nav.Item>
                    </div>
                </Nav>
            </Navbar.Collapse>
        </Navbar>
    </Styles>

        <Route exact path="/" component={Home} />
        <Route path="/cybersecurity-intro" component={CybersecurityIntro} /> 
        <Route path="/cybersecurity-and-cybercrimes" component={CybersecurityCybercrimes} />
        <Route path="/cryptography-intro" component={CryptographyIntro} />
        <Route path="/what-is-data" component={WhatIsData} />
        <Route path="/transfer-data-securely" component={TransferData} />
        <Route path="/encryption-and-decryption" component={EncryptionDecryption} />
        <Route path="/common-algorithms" component={CommonAlgorithms} />
        <Route path="/3des" component={Algorithm3DES} />
        <Route path="/aes" component={AlgorithmAES} />
        <Route path="/rsa" component={AlgorithmRSA} />
        <Route path="/blowfish" component={AlgorithmBlowfish} />
        <Route path="/twofish" component={AlgorithmTwofish} />
        <Route component={NoMatch} />

    </Router>
)
